#include &lt;bits/stdc++.h&gt;using namespace std;class MergeSort &#123;public:    int arr[50005];    int help[50005];    vector&lt;int&gt; sortArray(vector&lt;int&gt;&amp; nums) &#123;        if(nums.size() &lt;= 1) return nums;        for(int i=0; i&lt;nums.size(); ++i) arr[i] = nums[i];        mergeSort(0, nums.size()-1);                vector&lt;int&gt; ans;        for(int i=0; i&lt;nums.size(); ++i) ans.push_back(arr[i]);        return ans;    &#125;    void mergeSort(int l, int r)&#123;        if(l == r) return; // base case        int m = (l+r)/2;        mergeSort(l, m);        mergeSort(m+1, r);        doSort(l, m, r);    &#125;    void doSort(int l, int m, int r)&#123;        int i = l;        int a = l;        int b = m+1;        while(a &lt;= m &amp;&amp; b &lt;= r)&#123;            help[i++] = (arr[a] &lt;= arr[b]) ? arr[a++] : arr[b++];        &#125;        while(a &lt;= m)&#123;            help[i++] = arr[a++];        &#125;        while(b &lt;= r)&#123;            help[i++] = arr[b++];        &#125;        for(int i=l; i&lt;=r; ++i)&#123;            arr[i] = help[i];        &#125;        return;    &#125;&#125;;

class QuickSort &#123;public:    vector&lt;int&gt; arr;    int first, last;    vector&lt;int&gt; sortArray(vector&lt;int&gt;&amp; nums) &#123;        if(nums.size() &lt;= 1) return nums;        arr = nums;        srand(time(0));        quickSort(0, nums.size()-1);        return arr;    &#125;    void quickSort(int l, int r)&#123;        if(l &gt;= r) return;        int x = arr[l + rand()%(r-l+1)];        partition(l, r, x);        int left = first;        int right = last;        quickSort(l, left-1);        quickSort(right+1, r);    &#125;    void partition(int l, int r, int x)&#123;        first = l;        last = r;        int i=l;        while(i &lt;= last)&#123;            if(arr[i] &lt; x)&#123;                swap(first++, i++);            &#125;            else if(arr[i] == x)&#123;                ++i;            &#125;            else&#123;                swap(last--, i);            &#125;        &#125;    &#125;    void swap(int a, int b)&#123;        int tmp = arr[a];        arr[a] = arr[b];        arr[b] = tmp;    &#125;&#125;;

class HeapSort &#123;public:    vector&lt;int&gt; sortArray(vector&lt;int&gt;&amp; nums) &#123;        int n = nums.size();        for(int i=(n-1)/2; i &gt;= 0; --i)&#123;            // i正常來說是從n-1開始，但最底下那層的子樹都只有自己，一定是大根堆，所以從(n-1)/2開始            heapify(nums, i, n);            // 從底開始建樹        &#125;        // 排序        int size = n;        while(size &gt; 1)&#123;            swap(nums, 0, --size);            heapify(nums, 0, size);        &#125;        return nums;    &#125;    // 從上往下建才會用到，從下往上建不需要    void heapInsert(vector&lt;int&gt; &amp;nums, int i)&#123;        // 插入新元素往上看        while(nums[i] &gt; nums[(i-1)/2])&#123;            swap(nums, i, (i-1)/2);            i = (i-1)/2;        &#125;    &#125;    void heapify(vector&lt;int&gt;&amp; arr, int i, int size)&#123;        int l = i*2+1;        while(l &lt; size)&#123;            // 如果右孩子存在且值大於左孩子就讓winner等於右孩子，否則就是左孩子            int winner = (l+1 &lt; size) &amp;&amp; (arr[l+1] &gt; arr[l]) ? l+1 : l;            // 確認winner所在的值是否大於父親的值            winner = (arr[winner] &gt; arr[i]) ? winner : i;            // 如果孩子都比父親小就結束往下的排序            if(winner == i) break;            // 否則交換值，i繼續往下走(變成孩子)            swap(arr, winner, i);            i = winner;            // l繼續往下找有沒有孩子            l = i*2+1;        &#125;    &#125;    void swap(vector&lt;int&gt;&amp; arr, int a, int b)&#123;        int tmp = arr[a];        arr[a] = arr[b];        arr[b] = tmp;    &#125;&#125;;

